// StringTemplates/ExpressionInTemplate.kt

fun main(args: Array<String>) {
  val condition = true
  println(
    "${if (condition) 'a' else 'b'}")  // [1]
  val x = 11
  println("$x + 4 = ${x + 4}")
}
/* Output:
a
11 + 4 = 15
*/

// StringTemplates/StringConcatenation.kt

fun main(args: Array<String>) {
  val s = "hi\n"                // [1]
  val n = 11
  val d = 3.14
  println("first: " + s + "second: " +
    n + ", third: " + d)
}
/* Output:
first: hi
second: 11, third: 3.14
*/

