package numberTypes1

import org.junit.Assert
import org.junit.FixMethodOrder
import org.junit.Test
import org.junit.runners.MethodSorters

@FixMethodOrder(MethodSorters.NAME_ASCENDING)
class TestNumberTypes1 {
    @Test fun testA() {
        Assert.assertEquals(3, a)
    }

    @Test fun testB() {
        Assert.assertEquals(2, b)
    }

    @Test fun testC() {
        Assert.assertEquals(11, c)
    }

    @Test fun testD() {
        Assert.assertEquals(1.2, d.toDouble(), 0.0001)
    }
}

package numberTypes2

import org.junit.Assert
import org.junit.FixMethodOrder
import org.junit.Test
import org.junit.runners.MethodSorters

@FixMethodOrder(MethodSorters.NAME_ASCENDING)
class TestNumberTypes2 {
    @Test
    fun test1Million() {
        Assert.assertEquals(1_000_000, million)
    }

    @Test
    fun test2Billion() {
        Assert.assertEquals(1_000_000_000, billion)
    }

    @Test
    fun test3Trillion() {
        Assert.assertEquals(1_000_000_000_000, trillion)
    }

    @Test
    fun test4Quintillion() {
        Assert.assertEquals(1_000_000_000_000_000_000, quintillion)
    }

    @Test
    fun test5MillionType() {
        Assert.assertEquals("Int", millionType)
    }

    @Test
    fun test6BillionType() {
        Assert.assertEquals("Int", billionType)
    }

    @Test
    fun test7TrillionType() {
        Assert.assertEquals("Long", trillionType)
    }

    @Test
    fun test8QuintillionType() {
        Assert.assertEquals("Long", quintillionType)
    }
}

package numberTypes3

import org.junit.Assert
import org.junit.FixMethodOrder
import org.junit.Test
import org.junit.runners.MethodSorters

@FixMethodOrder(MethodSorters.NAME_ASCENDING)
class TestNumberTypes3 {
    private fun checkFahrenheitToCelsius(C: Double, F: Int) {
        Assert.assertEquals("$F F should be $C C",
                C, convertFahrenheitToCelsius(F), 0.0001)
    }

    private fun checkCelsiusToFahrenheit(F: Double, C: Int) {
        Assert.assertEquals("$C C should be $F F",
                F, convertCelsiusToFahrenheit(C), 0.0001)
    }

    @Test fun test1FahrenheitToCelsius() = checkFahrenheitToCelsius(-15.0, 5)

    @Test fun test2FahrenheitToCelsius() = checkFahrenheitToCelsius(37.7778, 100)

    @Test fun test3CelsiusToFahrenheit() = checkCelsiusToFahrenheit(5.0, -15)

    @Test fun test4CelsiusToFahrenheit() = checkCelsiusToFahrenheit(68.0, 20)
}

