// RepetitionWithWhile/AssignmentOperators.kt

fun main(args: Array<String>) {
  var n = 10
  val d = 3
  print(n)
  while (n > d) {
    n -= d
    print(" - $d")
  }
  println(" = $n")

  var m = 10
  print(m)
  m %= d
  println(" % $d = $m")
}
/* Output:
10 - 3 - 3 - 3 = 1
10 % 3 = 1
*/

// RepetitionWithWhile/DoWhileLoop.kt

fun main(args: Array<String>) {
  var i = 0
  do {
    print(".")
    i += 10
  } while (condition(i))
}
/* Output:
..........
*/

// RepetitionWithWhile/IncrementOperator.kt

fun main(args: Array<String>) {
  var i = 0
  while (i < 4) {
    print(".")
    i++
  }
}
/* Output:
....
*/

// RepetitionWithWhile/WhileLoop.kt

fun condition(i: Int) = i < 100

fun main(args: Array<String>) {
  var i = 0
  while (condition(i)) {
    print(".")
    i += 10       // [1]
  }
}
/* Output:
..........
*/

