import org.junit.Assert import org.junit.FixMethodOrder import org.junit.Test import org.junit.runners.MethodSorters import java.io.ByteArrayOutputStream import java.io.PrintStream
@FixMethodOrder(MethodSorters.NAME_ASCENDING) class TestHalfPyramid { private fun checkOutput(n: Int, expected: String) { val byteArrayOutputStream = ByteArrayOutputStream() System.setOut(PrintStream(byteArrayOutputStream)) printHalfPyramid(n) Assert.assertEquals("Incorrect output for n = $n:", expected, byteArrayOutputStream.toString()) }
