val isOpen = hour >= opens && hour <= closes println("Open: " + isOpen) println("Not open: " + !isOpen) val isClosed = hour < opens || hour > closes println("Closed: " + isClosed) } /* Output: Operating hours: 9 - 20 Current time: 6 Open: false Not open: true Closed: true */

