@FixMethodOrder(MethodSorters.NAME_ASCENDING) class TestSnake { private fun checkOutput(rows: Int, columns: Int, expected: String) { val byteArrayOutputStream = ByteArrayOutputStream() System.setOut(PrintStream(byteArrayOutputStream)) printSnake(rows, columns) Assert.assertEquals("Incorrect output for rows = $rows, columns = $columns:", expected, byteArrayOutputStream.toString()) }
@Test fun testSnake2() = checkOutput(2,2, """ | 0 1 | 3 2 | """.trimMargin())
