    @Test fun testPalindrome8() = checkPalindrome("a.", false)
}

var onlyLetters = ""
    for (c in s) {
        if (c in 'a'..'z' || c in 'A'..'Z') {
            onlyLetters += c
        }
    }
    return isPalIgnoreCase(onlyLetters)

