// CreatingClasses/Hamster.kt

class Hamster {
  fun speak() = "squeak! "
  fun exercise() =
    this.speak() + // Qualified with 'this'
    speak() +      // Without 'this'
    "Running on wheel"
}

fun main(args: Array<String>) {
  val hamster = Hamster()
  println(hamster.exercise())
}
/* Output:
squeak! squeak! Running on wheel
*/

package creatingClasses1

/**
 * The `SimpleString` class is a toy class
 * for representing strings used in AtomicKotlin course.
 */
class SimpleString(private val s: String) {

    /** Returns the `Char` value at the specified [index].
     */
    fun get(index: Int): Char = s[index]

    /**
     * Returns the length of this string.
     */
    fun length() = s.length
}

for (i in 0 until s.length()) {
        if (s.get(i) != s.get(s.length() - 1 - i)) return false
    }
    return true

