// Lists/ListUsefulFunction.kt import atomictest.eq
fun main(args: Array<String>) { val doubles = listOf(1.1, 2.2, 3.3, 4.4) doubles.min() eq 1.1 doubles.max() eq 4.4
val strings = listOf("Twas", "Brillig", "And", "Slithy", "Toves") strings eq listOf("Twas", "Brillig", "And", "Slithy", "Toves") strings.sorted() eq listOf("And", "Brillig", "Slithy", "Toves", "Twas") strings.reversed() eq listOf("Toves", "Slithy", "And", "Brillig", "Twas") strings.first() eq "Twas" strings.takeLast(2) eq listOf("Slithy", "Toves") }

// Lists/Lists.kt
import atomictest.eq

fun main(args: Array<String>) {
  // Lists hold other objects:
  val ints = listOf(99, 3, 5, 7, 11, 13)
  ints eq "[99, 3, 5, 7, 11, 13]"      // [1]

  // Select each element in the List:
  var result = ""
  for (i in ints)                      // [2]
    result += "$i "
  result eq "99 3 5 7 11 13 "

  // "[]" is "Indexing":
  ints[4] eq 11                        // [3]
}

// Lists/MultipleListRefs.kt
import atomictest.eq

fun main(args: Array<String>) {
  val first = mutableListOf(1)
  val second: List<Int> = first
  second eq listOf(1)

  first += 2
  // second sees the change:
  second eq listOf(1, 2)
}

// Lists/MutListIsList.kt
import atomictest.eq

fun getList(): List<Int> {
  return mutableListOf(1, 2, 3)
}

fun main(args: Array<String>) {
  // getList() produces a read-only List:
  val list = getList()
  // list += 3 // Error
  list eq listOf(1, 2, 3)
}

// Lists/MutableList.kt
import atomictest.eq

fun main(args: Array<String>) {
  val list = mutableListOf<Int>()

  list.add(1)
  list.addAll(listOf(2, 3))

  list += 4
  list += listOf(5, 6)

  list eq listOf(1, 2, 3, 4, 5, 6)
}

