override fun iterator(): Iterator<Int> = list.iterator()
operator fun get(index: Int): Int = list[index]
override fun toString() = list.toString()
fun size() = list.size }

var max = 0
    for (e in list) {
        if (max <= e) {
            max = e
        }
    }
    return max

