// Maps/ContactMap.kt
import atomictest.eq

class Contact(
  val name: String,
  val number: String
) {
  override fun toString(): String {
    return "Contact('$name', '$number')"
  }
}

fun main(args: Array<String>) {
  val miffy = Contact("Miffy", "1-234-567890")
  val cleo = Contact("Cleo", "098-765-4321")
  val contacts = mapOf(
    miffy.number to miffy,
    cleo.number to cleo)
  contacts["1-234-567890"] eq miffy
  contacts["1-111-111111"] eq null
}

// Maps/GetValue.kt
import atomictest.*

fun main(args: Array<String>) {
  val map = mapOf('a' to "attempt")
  map['b'] eq null
  capture {
    map.getValue('b')
  } eq "NoSuchElementException: " +
    "Key b is missing in the map."
}

// Maps/Maps.kt import atomictest.eq
fun main(args: Array<String>) { val constants = mapOf( "Pi" to 3.141, "e" to 2.718, "phi" to 1.618) constants eq "{Pi=3.141, e=2.718, phi=1.618}"
// Look up a value from a key: constants["e"] eq 2.718 // [1] constants.keys eq setOf("Pi", "e", "phi") constants.values eq "[3.141, 2.718, 1.618]"
var s = "" // Iterate through key-value pairs: for (entry in constants) // [2] s += "${entry.key}=${entry.value}, " s eq "Pi=3.141, e=2.718, phi=1.618, "
s = "" // Unpack during iteration: for ((key, value) in constants) // [3] s += "$key=$value, " s eq "Pi=3.141, e=2.718, phi=1.618, " }

// Maps/ReadOnlyMaps.kt
import atomictest.eq

fun main(args: Array<String>) {
  val m = mapOf(5 to "five", 6 to "six")
  m[5] eq "five"
  // m[5] = "5ive" // Fails
  // m += (4 to "four") // Fails
  m + (4 to "four") // Doesn't change m
  m eq mapOf(5 to "five", 6 to "six")
  val m2 = m + (4 to "four")
  m2 eq mapOf(
    5 to "five", 6 to "six", 4 to "four")
}

