// Overloading/MemberVsExtension.kt
import atomictest.eq

class My {
  fun foo() = 0
}

fun My.foo() = 1             // [1]

fun My.foo(i: Int) = i + 2   // [2]

fun main(args: Array<String>) {
  My().foo() eq 0
  My().foo(1) eq 3
}

// Overloading/Overloading.kt
import atomictest.eq

class Overloading {
  fun f() = 0
  fun f(n: Int) = n + 2
}

fun main(args: Array<String>) {
  val o = Overloading()
  o.f() eq 0
  o.f(11) eq 13
}

// Overloading/OverloadingAdd.kt import atomictest.eq
fun addInt(i: Int, j: Int) = i + j fun addDouble(i: Double, j: Double) = i + j
fun add(i: Int, j: Int) = i + j fun add(i: Double, j: Double) = i + j
fun main(args: Array<String>) { addInt(5, 6) eq add(5, 6) addDouble(56.23, 44.77) eq add(56.23, 44.77) }

