val result = mutableMapOf<Author, MutableList<Book>>()
    for (book in books) {
        for (author in book.authors) {
            if (!result.containsKey(author)) {
                result[author] = mutableListOf()
            }
            result.getValue(author) += book
        }
    }
    return result

