package extensionsforNullableTypes1

import org.junit.Assert
import org.junit.FixMethodOrder
import org.junit.Test
import org.junit.runners.MethodSorters

@FixMethodOrder(MethodSorters.NAME_ASCENDING)
class TestOrEmpty {
    private fun checkExpectedValue(s: String?, value: String) {
        Assert.assertEquals("Wrong value for $s:", value, s.orEmpty())
    }

    @Test fun test1Sample() = checkExpectedValue(null, "")

    @Test fun test2Sample() = checkExpectedValue("abc", "abc")

    @Test fun test3() = checkExpectedValue("s", "s")
}

package extensionsforNullableTypes2

import org.junit.Assert
import org.junit.FixMethodOrder
import org.junit.Test
import org.junit.runners.MethodSorters

@FixMethodOrder(MethodSorters.NAME_ASCENDING)
class TestExtensionsForNullable {

    private fun checkValues(receiver: Boolean?, argument: Boolean?) {
        Assert.assertEquals("Wrong result for $receiver.and($argument)",
                receiver.and(argument), expectedAnd(receiver, argument))
        Assert.assertEquals("Wrong result for $receiver.or($argument)",
                receiver.or(argument), expectedOr(receiver, argument))
    }

    private fun expectedAnd(receiver: Boolean?, argument: Boolean?): Boolean? =
            if (receiver != null && argument != null) receiver && argument else null

    private fun expectedOr(receiver: Boolean?, argument: Boolean?): Boolean? =
            if (receiver != null && argument != null) receiver || argument else null

    @Test
    fun testAll() {
        val booleans = listOf(true, false)
        for (first in booleans) {
            for (second in booleans) {
                checkValues(first, second)
            }
        }
    }
}

// IntroGenerics/AnyInstead.kt
package introgenerics
import atomictest.eq

class AnyHolder(private val value: Any) {
  fun getValue(): Any = value
}

class Dog {
  fun bark() = "Ruff!"
}

fun main(args: Array<String>) {
  val holder = AnyHolder(Dog())
  val any = holder.getValue()
  // Won't compile:
  // any.bark()

  val genericHolder = GenericHolder(Dog())
  val dog = genericHolder.getValue()
  dog.bark() eq "Ruff!"
}

// IntroGenerics/GenericFunction.kt
package introgenerics
import atomictest.eq

fun <T> identity(arg: T): T = arg

fun main(args: Array<String>) {
  identity("Yellow") eq "Yellow"
  identity(1) eq 1
  identity(Dog()).bark() eq "Ruff!"
}

// IntroGenerics/GenericHolder.kt
package introgenerics
import atomictest.eq

class GenericHolder<T>(              // [1]
  private val value: T
) {
  fun getValue(): T = value
}

fun main(args: Array<String>) {
  val h1 = GenericHolder(Automobile("Ford"))
  val a: Automobile = h1.getValue()  // [2]
  a eq "Automobile(brand=Ford)"

  val h2 = GenericHolder(1)
  val i: Int = h2.getValue()         // [3]
  i eq 1

  val h3 = GenericHolder("Chartreuse")
  val s: String = h3.getValue()      // [4]
  s eq "Chartreuse"
}

// IntroGenerics/GenericListExtensions.kt
package introgenerics
import atomictest.eq

fun <T> List<T>.first(): T {
  if (isEmpty())
    throw NoSuchElementException("Empty List")
  return this[0]
}

fun <T> List<T>.firstOrNull(): T? {
  return if (isEmpty()) null else this[0]
}

fun main(args: Array<String>) {
  listOf(1, 2, 3).first() eq 1

  val i: Int? =                     // [1]
    listOf(1, 2, 3).firstOrNull()
  i eq 1

  val s: String? =                  // [2]
    listOf<String>().firstOrNull()
  s eq null
}

// IntroGenerics/RigidHolder.kt
package introgenerics
import atomictest.eq

data class Automobile(val brand: String)

class RigidHolder(private val a: Automobile) {
  fun getValue() = a
}

fun main(args: Array<String>) {
  val holder = RigidHolder(Automobile("BMW"))
  holder.getValue() eq
    "Automobile(brand=BMW)"
}

package introductiontoGenerics1

import org.junit.Assert
import org.junit.FixMethodOrder
import org.junit.Test
import org.junit.runners.MethodSorters

@FixMethodOrder(MethodSorters.NAME_ASCENDING)
class TestSecond {
    private fun <T> checkSecondFunctions(list: List<T>, second: T?, penultimate: T?) {
        Assert.assertEquals("Wrong `second` element for $list:",
                list.secondOrNull(), second)
        Assert.assertEquals("Wrong `penultimate` element for $list:",
                list.penultimateOrNull(), penultimate)
    }

    @Test
    fun test1Sample() {
        checkSecondFunctions(listOf(1, 2, 3), 2, 2)
    }

    @Test
    fun test2Sample() {
        checkSecondFunctions(listOf("a", "b", "c", "d"), "b", "c")
    }

    @Test
    fun test3() {
        checkSecondFunctions(listOf('a', 'b', 'c', 'd', 'e', 'f', 'x', 'y', 'z'), 'b', 'y')
    }

    @Test
    fun test4OneElement() {
        checkSecondFunctions(listOf(1), null, null)
    }

    @Test
    fun test5Empty() {
        checkSecondFunctions(listOf(), null, null)
    }
}

for (i in lastIndex downTo 0) {
        result += get(i)
    }

