@FixMethodOrder(MethodSorters.NAME_ASCENDING)
class TestGenerics {
    private fun <T> checkReversed(list: List<T>) {
        Assert.assertEquals("Wrong result for $list",
                list.reversed(), list.reversedLibrary()
        )
    }

