package memberReferences02

import org.junit.Assert
import org.junit.FixMethodOrder
import org.junit.Test
import org.junit.runners.MethodSorters

@FixMethodOrder(MethodSorters.NAME_ASCENDING)
class TestMapImpl {
    @Test
    fun test1Sample() {
        val range = 1..1000
        val even = range.filter(/*<taskWindow>*/Int::isEven/*</taskWindow>*/)
        Assert.assertEquals("Wrong result for `even` collection:", 500, even.size)

        val prime = range.filter(/*<taskWindow>*/Int::isPrime/*</taskWindow>*/)
        Assert.assertEquals("Wrong result for `prime` collection:",
                listOf(953, 967, 971, 977, 983, 991, 997), prime.takeLast(7))

        val perfect = range.filter(/*<taskWindow>*/::isPerfect/*</taskWindow>*/)
        Assert.assertEquals("Wrong result for `perfect` collection:",
                listOf(6, 28, 496), perfect)

    }

    private fun testPrime(i: Int, isPrime: Boolean) {
        Assert.assertEquals("The number $i ${if (isPrime) "should" else "shouldn't"} be prime", isPrime, i.isPrime())
    }

    @Test
    fun test2Prime() {
        testPrime(2, true)
        testPrime(5, true)
        testPrime(1861, true)
        testPrime(2399, true)
        testPrime(5569, true)
        testPrime(6737, true)
        testPrime(12007, true)

        testPrime(1, false)
        testPrime(10, false)
        testPrime(2397, false)
        testPrime(6739, false)
    }

    @Test
    fun test3Perfect() {
        val i = 8128
        Assert.assertTrue("The number $i should be perfect", isPerfect(i))
    }
}

// FunctionTypes/AnyFunImplementation.kt
import atomictest.eq

fun <T> List<T>.any(                    // [1]
  predicate: (T) -> Boolean             // [2]
): Boolean {
  for (element in this) {
    if (predicate(element))             // [3]
      return true
  }
  return false
}

fun main(args: Array<String>) {
  val ints = listOf(1, 2, -3)
  ints.any { it > 0 } eq true           // [4]

  val strings = listOf("abc", " ")
  strings.any { it.isBlank() } eq true  // [5]
  strings.any { it.isBlank() } eq true  // [5]

  strings.any(String::isNotBlank) eq    // [6]
    true
}

// FunctionTypes/DefiningRepeat.kt
package definingrepeat

fun repeat(
  times: Int,
  action: (Int) -> Unit           // [1]
) {
  for (index in 0 until times)
    action(index)                 // [2]
}

fun main(args: Array<String>) {
  repeat(3) { println("#$it") }   // [3]
}
/* Output:
#0
#1
#2
*/

// FunctionTypes/FunctionTypeExamples.kt
import atomictest.eq

fun main(args: Array<String>) {
  val helloWorld: () -> String =
    { "Hello, world!" }
  helloWorld() eq "Hello, world!"

  val sum: (Int, Int) -> Int =
    { x, y -> x + y }
  sum(1, 2) eq 3
}

// FunctionTypes/NullableFunctionType.kt
import atomictest.eq

fun main(args: Array<String>) {
  val returnTypeNullable: (String) -> Int? =
    { null }
  val mightBeNull: ((String) -> Int)? = null
  returnTypeNullable("abc") eq null
  // Won't compile without a null check:
  // mightBeNull("abc")
  if (mightBeNull != null) {
    mightBeNull("abc")
  }
}

// FunctionTypes/NullableReturnType.kt
import atomictest.eq

fun main(args: Array<String>) {
  val transform: (String) -> Int? =
    { s: String -> s.toIntOrNull() }
  transform("112") eq 112
  transform("abc") eq null

  val list = listOf("112", "abc")
  list.mapNotNull(transform) eq listOf(112)
  list.mapNotNull { it.toIntOrNull() } eq
    listOf(112)
}

