package functionTypes4
import org.junit.Assert import org.junit.FixMethodOrder import org.junit.Test import org.junit.runners.MethodSorters
@FixMethodOrder(MethodSorters.NAME_ASCENDING) class TestAndThen { private fun <A, B, C> check( arg: A, f: (A) -> B, g: (B) -> C, fStr: String, gStr: String ) { Assert.assertEquals("Wrong result for f(g(arg))\n" + "arg = $arg\n" + "f = $fStr\n" + "g = $gStr", g(f(arg)), f.andThen(g)(arg)) }
@Test fun test1() = check("123", f = { s: String -> s.toInt() }, g = { i: Int -> i + 1 }, fStr = "{ s: String -> s.toInt() }", gStr = "{ i: Int -> i + 1 }")
@Test fun test2() = check(111, f = { i: Int -> i + 1 }, g = { i: Int -> i.toString() }, fStr = "{ i: Int -> i + 1 }", gStr = "{ i: Int -> i.toString() }")
@Test fun test3() = check(listOf(1, 2, 3), f = { l: List<Int> -> l.map { it + 1 } }, g = { l: List<Int> -> l.joinToString() }, fStr = "{ l: List<Int> -> l.map { it + 1 } }", gStr = "{ l: List<Int> -> l.joinToString() }")
@Test fun test4() = check("ABC", f = { s: String -> s.toIntOrNull() }, g = { i: Int? -> i ?: 0 }, fStr = "{ s: String -> s.toIntOrNull() }", gStr = "{ i: Int? -> i ?: 0 }") }

// ManipulatingLists/Flatten.kt
import atomictest.eq

fun main(args: Array<String>) {
  val list = listOf(
    listOf(1, 2, 3),
    listOf(4, 5, 6))
  list.flatten() eq listOf(1, 2, 3, 4, 5, 6)
}

// ManipulatingLists/FlattenAndFlatMap.kt
import atomictest.eq

fun main(args: Array<String>) {
  val intRange = 1..3

  intRange.map {               // [1]
    a -> intRange.map { b -> a to b }
  } eq "[" +
    "[(1, 1), (1, 2), (1, 3)], " +
    "[(2, 1), (2, 2), (2, 3)], " +
    "[(3, 1), (3, 2), (3, 3)]" +
  "]"

  intRange.map {               // [2]
    a -> intRange.map { b -> a to b }
  }.flatten() eq "[" +
    "(1, 1), (1, 2), (1, 3), " +
    "(2, 1), (2, 2), (2, 3), " +
    "(3, 1), (3, 2), (3, 3)" +
  "]"

  intRange.flatMap {           // [3]
    a -> intRange.map { b -> a to b }
  } eq "[" +
    "(1, 1), (1, 2), (1, 3), " +
    "(2, 1), (2, 2), (2, 3), " +
    "(3, 1), (3, 2), (3, 3)" +
  "]"
}

// ManipulatingLists/WhyFlatMap.kt
package manipulatinglists
import atomictest.eq

class Book(
  val title: String,
  val authors: List<String>
)

fun main(args: Array<String>) {
  val books = listOf(
    Book("1984", listOf("George Orwell")),
    Book("Ulysses", listOf("James Joyce"))
  )
  books.map { it.authors }.flatten() eq
    listOf("George Orwell", "James Joyce")

  books.flatMap { it.authors } eq
    listOf("George Orwell", "James Joyce")
}

// ManipulatingLists/ZipAndTransform.kt
package manipulatinglists
import atomictest.eq

data class Person(
  val name: String,
  val id: Int
)

fun main(args: Array<String>) {
  val names = listOf("Bob", "Jill", "Jim")
  val ids = listOf(1731, 9274, 8378)
  names.zip(ids) {
    name, id -> Person(name, id)
  } eq "[Person(name=Bob, id=1731), " +
    "Person(name=Jill, id=9274), " +
    "Person(name=Jim, id=8378)]"
}

// ManipulatingLists/Zipper.kt import atomictest.eq
fun main(args: Array<String>) { val left = listOf("a", "b", "c", "d") val right = listOf("q", "r", "s", "t")
left.zip(right) eq // [1] "[(a, q), (b, r), (c, s), (d, t)]"
left.zip(0..4) eq // [2] "[(a, 0), (b, 1), (c, 2), (d, 3)]"
(10..100).zip(right) eq // [3] "[(10, q), (11, r), (12, s), (13, t)]" }

