  stringBuilder.append("Alphabet: ")   // [2]
  for (ch in 'a'..'z')
    stringBuilder.append(ch)           // [3]
  stringBuilder.toString() eq          // [4]
    "Alphabet: abcdefghijklmnopqrstuvwxyz"
}

