// WithFunction/Repetitive.kt
package withfunction
import atomictest.eq

class Window(
  val id: String,
  var x: Int = 0,
  var y: Int = 0,
  var width: Int = 200,
  var height: Int = 100
)

fun adjustWindow(window: Window) {
