// LibraryHelpers/NullableReceiver.kt
package libraryhelpers

data class Window(  // Makes it displayable
  val id: String = "none",
  var x: Int = 0,   // Horizontal location
  var y: Int = 0,   // Vertical Location
  var w: Int = 200, // Width
  var h: Int = 100  // Height
)

fun adjustWindowIfNotNull(window: Window?) {
  window?.run {          // [1]
    this.x = 10          // [2]
    y = 10
    w *= 2
    h *= 2
    println(this)
  }
}

fun main(args: Array<String>) {
  val windowOrNull: Window? = null
  // Nothing happens:
  adjustWindowIfNotNull(windowOrNull)
}

// LibraryHelpers/ResultOfApply.kt
package libraryhelpers
import atomictest.eq

fun main(args: Array<String>) {
  val mutableList = mutableListOf<Int>()
  val result = mutableList.apply {
    add(17)
    add(42)
    remove(0)
  }
  result eq listOf(17, 42)
  mutableList eq listOf(17, 42)
}

// LibraryHelpers/ResultOfWith.kt
package libraryhelpers
import atomictest.eq

fun main(args: Array<String>) {
  val mutableList = mutableListOf<Int>()
  val result = with(mutableList) {
    add(17)
    add(42)
    remove(0)
  }
  result eq false
  mutableList eq listOf(17, 42)
}

x.let {
    it.first = 1
    it.second = 2
    it.third = 3
}

with(x) {
        first = 1
        second = 2
        third = 3
    }

y?.let {
    with(it) {
        start()
        finish()
    }
}

y?.run {
        start()
        finish()
    }

val result = with(z) {
    init()
    this
}

val result = z.apply {
       init()
   }

package libraryHelpers1

class X {
    var first = 0
    var second = 0
    var third = 0
}

fun example1(x: X) {

    x.let {
        it.first = 1
        it.second = 2
        it.third = 3
    }

}

class Y {
    fun start() {}
    fun finish() {}
}

fun example2(y: Y?) {

    y?.let {
        with(it) {
            start()
            finish()
        }
    }

}

class Z {
    fun init() {}
}

fun example3(z: Z) {

    val result = with(z) {
        init()
        this
    }

}

fun main(args: Array<String>) {
    example1(X())
    example2(Y())
    example3(Z())
}

