fun main(args: Array<String>) {
    val sb = StringBuilder()
    with (sb) {
        append("0")
    }
    sb.toString() eq "0"

    StringBuilder().run {
        append("1")
        toString()
    } eq "1"

    StringBuilder().let {
        it.append("2")
        it.toString()
    } eq "2"

    StringBuilder().apply {
        append("3")
    }.toString() eq "3"

    StringBuilder().also {
        it.append("4")
    }.toString() eq "4"

    class Rectangle(var width: Int = 0, var height: Int = 0)
    val map = mapOf("square" to Rectangle())
    val square = map["square"]?.apply {
        width = 20
        height = 20
    }
    square?.width eq 20
    square?.height eq 20
}

