// For comparison
fun fibonacciRecursive(n: Int): Long {
    tailrec fun fibonacci(
            n: Int,
            current: Long,
            next: Long
    ): Long {
        if (n == 0) return current
        return fibonacci(
                n - 1, next, current + next)
    }

    return fibonacci(n, 0L, 1L)
}

